import React, { Component } from 'react';

import NavigationBar from './NavigationBar';
import PathBar from "./PathBar";
import DLButton from "./DLButton";

import renderSkill from './renderSkill';

import streamy from '../images/streamy.png';
import swipe from '../images/swipe.png'
import blog from '../images/blog.png'

import illustrator from '../images/skill-icons/Ai.svg';
import css from '../images/skill-icons/css.svg';
import html5 from '../images/skill-icons/html5.svg';
import java from '../images/skill-icons/java.svg';
import photoshop from '../images/skill-icons/ps.svg';
import react from '../images/skill-icons/react.svg';
import javascript from '../images/skill-icons/javascript.svg';
import nodejs from '../images/skill-icons/nodejs.svg';
import mongodb from '../images/skill-icons/mongodb.svg';
import android from '../images/skill-icons/android.svg';

import './App.css';
import './IntroductionAnimation.scss';
import './Skills.scss'


/* name obj array for skills icons for the Project cards. */
const arraySkills = [
    {name: 'react', obj: react},
    {name: 'illustrator', obj: illustrator},
    {name: 'css', obj: css},
    {name: 'html5', obj: html5},
    {name: 'java', obj: java},
    {name: 'photoshop', obj: photoshop},
    {name: 'javascript', obj: javascript},
    {name: 'nodejs', obj: nodejs},
    {name: 'mongodb', obj: mongodb},
    {name: 'android', obj: android}
];

class Home extends Component {
    render() {
        return (
            <div className="global">
                <div className="App">
                    <NavigationBar />
                    <div className="intro">
                        <div className="intro-container">
                            <h1>
                                Hello, my name is Ahmed.
                                {this.renderIntroduction()}
                            </h1>
                        </div>
                    </div>
                    <DLButton />
                    <PathBar />
                </div>

                <p className="align-self-center section-heading">
                    Past Projects
                </p>

                {this.renderProject(
                    ['react', 'javascript', 'html5', 'css'],
                    streamy,
                    'Streamy',
                    'A functioning streaming website similar to twitch.tv compatible \n' +
                    'with popular broadcasting software (OBS, Streamlabs etc.)',
                    'https://github.com/AhmedAkbarali/Streaming-website',
                    {background: `linear-gradient(135deg, rgb(53, 16, 78) 0%, rgb(43, 50, 178) 100%)`}
                )}

                {this.renderProject(
                    ['java'],
                    swipe,
                    'Swipe-To-Delete',
                    'A game designed for a User Study in order to quantify the value of\n' +
                    'swiping to delete objects like emails in mobile applications.',
                    'https://github.com/AhmedAkbarali/Swipe-To-Delete',
                    {background: `linear-gradient(135deg, #087e8b 0%, rgb(67, 187, 245) 100%)`}

                )}

                {this.renderProject(
                    ['react', 'javascript', 'html5', 'css'],
                    blog,
                    'Blog',
                    'Blog website populated with dummy data using jsonPlaceholder.\n' +
                    'An educational project to learn how to use Redux. (Specifically Actions, Reducers and Store.)',
                    'https://github.com/AhmedAkbarali/blogs',
                    {background: `linear-gradient(135deg, #ba5c12 0%, rgb(139,69,19) 100% )`}
                )}

                <div className="App skill-font">
                    <p className="align-self-center section-heading">
                        Skills
                    </p>

                    <p style={{marginTop: '50px'}}>
                        What interests me most right now:
                    </p>
                    <div className="skill-section" style={{fontSize: '32px'}}>
                        {renderSkill(['react', 'javascript', 'html5', 'css', 'mongodb', 'nodejs'])}
                    </div>
                    <p style={{marginTop: '50px'}}>
                        Skills I have experience with:
                    </p>

                    <div style={{fontSize: '32px'}}>
                        {renderSkill(['java', 'android', 'photoshop', 'illustrator'])}
                    </div>
                </div>

                <div className="App container">
                    <p className="section-heading">
                        About
                    </p>
                    <p>
                        My name is Ahmed Akbarali. I'm a Computer Science B.Sc from York University. <br/>
                        My focus is on Web Applications and websites, using React and NodeJS.
                    </p>
                </div>

                <footer>
                    Hello
                </footer>
            </div>
        );
    }





    /* Renders a formatted project card using information given. */
    renderProject(skills, image, title, description, link, styles) {
        return (
            <div
                className="work"
                style={styles}
            >
                {/* <img className="work-image" src={image} alt={title} /> */}


                <p className="work-header " style={{fontSize: '2em'}}>
                    {title}
                </p>

                {renderSkill(skills, title)}

                <p className="description" style={{fontSize: '20px'}}>
                    {description}
                </p>
                <div className="work-more">
                    <a className="work-more-text text-white" href={link} rel="noopener noreferrer" target="_blank"> View More </a>
                </div>
            </div>
        );
    }


    /* Introductory heading with animated css (text change) */
    renderIntroduction() {
        return (
            <div className="content">
                <div className="content__container">
                    <p className="content__container__text">
                        I'm a
                    </p>
                    <ul className="content__container__list">
                        <li className="content__container__list__item">Web Developer</li>
                        <li className="content__container__list__item">Coffee Lover</li>
                        <li className="content__container__list__item">Recent Graduate</li>
                        <li className="content__container__list__item">Traveller</li>
                    </ul>
                </div>
            </div>
        );
    }
}

export default Home;
