import React, { Component } from 'react';
import { Link } from "react-router-dom";

import NavigationBar from './NavigationBar';
import PathBar from "./PathBar";
import DLButton from "./DLButton";

import streamy from '../images/streamy.png';
import swipe from '../images/swipe.png'
import blog from '../images/blog.png'

import ai from '../images/skill-icons/Ai.svg';
import css from '../images/skill-icons/css.svg';
import html5 from '../images/skill-icons/html5.svg';
import java from '../images/skill-icons/java.svg';
import ps from '../images/skill-icons/ps.svg';
import rct from '../images/skill-icons/react.svg';

import './App.css';
import './IntroductionAnimation.scss';

class Home extends Component {
    render() {
        return (
            <div className="global">
                <div className="App">
                    <NavigationBar />
                    <div className="intro">
                        <div className="intro-container">
                            <h1>
                                Hello, my name is Ahmed.
                                {this.renderIntroduction()}
                            </h1>
                        </div>
                    </div>
                    <DLButton />
                    <PathBar />
                </div>

                <p className="align-self-center selected-work">
                    Past Projects
                </p>

                {this.renderProject(
                    [rct, html5, css, java],
                    streamy,
                    'Streamy',
                    'A functioning streaming website similar to twitch.tv compatible \n' +
                    'with popular broadcasting software (OBS, Streamlabs etc.)',
                    'https://github.com/AhmedAkbarali/Streaming-website',
                    {background: `linear-gradient(135deg, rgb(53, 16, 78) 0%, rgb(43, 50, 178) 100%)`}
                )}

                {this.renderProject(
                    [java],
                    swipe,
                    'Swipe-To-Delete',
                    'A game designed for a User Study in order to quantify the value of\n' +
                    'swiping to delete objects like emails in mobile applications.',
                    'https://github.com/AhmedAkbarali/Swipe-To-Delete',
                    {background: `linear-gradient(135deg, rgb(67, 187, 211) 0%, rgb(67, 187, 245) 100%)`}

                )}

                {this.renderProject(
                    [rct, html5, css, java],
                    blog,
                    'Blog',
                    'Blog website populated with dummy data using jsonPlaceholder.\n' +
                    'An educational project to learn how to use redux.\n' +
                    'Specifically Actions, Reducers and Store.',
                    'https://github.com/AhmedAkbarali/blogs',
                    {background: `linear-gradient(135deg, rgb(235, 203, 172) 0%, rgb(139,69,19) 100% )`}
                )}
            </div>
        );
    }


    /* Renders icons corresponding to the skills given */
    renderSkills(skills) {
        const listItems = skills.map((skills) =>
            <div className="skill" data-skill="JavaScript">
                <div className="skills-image">
                    <img  src={skills} alt={skills.toString()}/>
                </div>
            </div>
        );

        return(
            <div className="skills-container">
                {listItems}
            </div>
        );
    }


    /* Renders a formatted project card using information given. */
    renderProject(skills, image, title, description, link, styles) {
        return (
            <div
                className="work"
                style={styles}
            >
                <img className="work-image" src={image} alt={title} />
                <p className="work-header " style={{fontSize: '2em'}}>
                    {title}
                </p>

                {this.renderSkills(skills, title)}

                <p className="description" style={{fontSize: '20px'}}>
                    {description}
                </p>
                <div className="work-more">
                    <Link className="work-more-text text-white" to={link}> View More </Link>
                </div>
            </div>
        );
    }


    /* Introductory heading with animated css (text change) */
    renderIntroduction() {
        return (
            <div className="content">
                <div className="content__container">
                    <p className="content__container__text">
                        I'm a
                    </p>
                    <ul className="content__container__list">
                        <li className="content__container__list__item">Web Developer</li>
                        <li className="content__container__list__item">Coffee Lover</li>
                        <li className="content__container__list__item">Recent Graduate</li>
                        <li className="content__container__list__item">Traveller</li>
                    </ul>
                </div>
            </div>
        );
    }
}

export default Home;
